mapper.insert:
INSERT INTO COMM_LIKE(LIKE_NO, BOARD_NO, MEMBER_NO) 
VALUES (
    (SELECT MAX_LIKE_NO + 1 FROM (SELECT IFNULL(MAX(LIKE_NO), 0) AS MAX_LIKE_NO FROM COMM_LIKE) AS temp_table),
    #{board_no},
    #{member_no}
)

mapper.update:
UPDATE BOARD set 
LIKEHIT = LIKEHIT+1
WHERE BOARD_NO = #{board_no}

UPDATE BOARD set 
LIKEHIT = LIKEHIT - 1
where BOARD_NO = #{board_no}
